import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { artistasServices } from 'src/app/services/artistasServices';

@Component({
  selector: 'app-forms',
  templateUrl: './forms.component.html',
  styleUrls: ['./forms.component.css']
})

export class FormsComponent {

  artistas: Artistas[] = [];

  idArtista: FormControl = new FormControl('', Validators.required);
  nombreArtista: FormControl = new FormControl('', Validators.required);
  nombreAKA: FormControl = new FormControl('', [Validators.minLength(5), Validators.maxLength(12)]);
  estiloMusical: FormControl = new FormControl('', Validators.required);
  correo: FormControl = new FormControl('', Validators.email);
  telefono: FormControl = new FormControl('', Validators.max(9));
  fechaDeRegistro: FormControl = new FormControl('', Validators.required);
  mensaje: FormControl = new FormControl('', Validators.required);
  aceptarTerminos: FormControl = new FormControl('', Validators.required);

  Form: FormGroup = new FormGroup({
    idArtista: this.idArtista,
    nombreArtista: this.nombreArtista,
    nombreAKA: this.nombreAKA,
    estiloMusical: this.estiloMusical,
    correo: this.correo,
    telefono: this.telefono,
    fechaDeRegistro: this.fechaDeRegistro,
    mensaje: this.mensaje,
    aceptarTerminos: this.aceptarTerminos
  });

  constructor(private artistasService: artistasServices) { }
  ngOnInit() { }
  Clic(datos: FormGroup) {
    this.artistasService.signArtists(this.idArtista.value, this.nombreArtista.value, this.fechaDeRegistro.value, this.estiloMusical.value, this.correo.value, this.telefono.value, this.nombreAKA.value, this.mensaje.value, this.aceptarTerminos.value);
  }
}

export interface Artistas { idArtista: number, nombreArtista: string, fechaDeRegistro: string, estiloMusical: string, correo: string, telefono: number, nombreAKA: string, mensaje: string, aceptarTerminos: boolean }
