import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { BodyComponent } from './content/body/body.component';
import { Section2Component } from './content/section2/section2.component';
import { FormsComponent } from './content/forms/forms.component';

const routes: Routes = [
  { 
    path: 'inicio',
    component: BodyComponent
  },
  {
    path: 'forms',
    component: FormsComponent
  },
  {
    path: 'info',
    component: Section2Component
  }
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
